import React from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";  // ‚úÖ Use "../" to go up one level

const Sidebar = ({ role }) => {
  const navigate = useNavigate(); // React Router navigation

  return (
    <nav className="col-md-3 col-lg-2 d-md-block sidebar">
      <div className="position-sticky">
        <h2 className="text-center py-3">üéì LMS</h2>
        <ul className="nav flex-column">
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/dashboard")}>üìä Dashboard</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/courses")}>üìö Courses</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/assessments")}>üìù Assessments</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/messages")}>üí¨ Messages</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/forum")}>üíª Discussion Forum</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/calendar")}>üìÖ Calendar</button></li>
          {role === "admin" && <li className="nav-item"><button className="nav-link" onClick={() => navigate("/analytics")}>üìà Analytics</button></li>}
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/profile")}>üë§ Profile</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/settings")}>‚öôÔ∏è Settings</button></li>
          <li className="nav-item"><button className="nav-link" onClick={() => navigate("/support")}>‚ùì Help & Support</button></li>
        </ul>
      </div>
    </nav>
  );
};

export default Sidebar;
