import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

const Profile = () => {
  const navigate = useNavigate();
  const [personalInfo, setPersonalInfo] = useState(null);

  // Fetch Personal Info from API
  useEffect(() => {
    axios.get("http://localhost:5000/api/profile/personal-info")
      .then(response => setPersonalInfo(response.data))
      .catch(error => console.error("Error fetching personal info:", error));
  }, []);

  const profileData = [
    { title: "ðŸ‘¤ Personal Info", desc: "View and edit your personal details.", path: "/profile/personal-info" },
    { title: "ðŸ“š Enrolled Courses", desc: "Check the list of your active courses.", path: "/profile/courses" },
    { title: "ðŸ“Š Progress Report", desc: "Monitor your learning progress and achievements.", path: "/profile/progress" },
    { title: "ðŸ”’ Change Password", desc: "Update your account security settings.", path: "/profile/change-password" },
  ];

  return (
    <section className="container mt-5">
      <h1 className="text-center">Profile</h1>

      {/* Personal Info Card */}
      {personalInfo ? (
        <div className="card shadow-sm p-4 mb-4">
          <h3>ðŸ‘¤ Personal Info</h3>
          <p><strong>Full Name:</strong> {personalInfo.fullName}</p>
          <p><strong>Email:</strong> {personalInfo.email}</p>
          <p><strong>Phone:</strong> {personalInfo.phone}</p>
          <p><strong>Date of Birth:</strong> {personalInfo.dob}</p>
          <p><strong>Gender:</strong> {personalInfo.gender}</p>
          <p><strong>Role:</strong> {personalInfo.role}</p>
          <button className="btn btn-primary" onClick={() => navigate("/profile/personal-info")}>
            Manage Personal Info
          </button>
        </div>
      ) : (
        <p className="text-center">Loading personal info...</p>
      )}

      {/* Other Profile Sections */}
      <div className="row">
        {profileData.map((data, index) => (
          <div key={index} className="col-lg-4 col-md-6 mb-4">
            <div className="card h-100 shadow-sm">
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{data.title}</h5>
                <p className="card-text flex-grow-1">{data.desc}</p>
                <button className="btn btn-primary mt-auto" onClick={() => navigate(data.path)}>
                  Manage
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Profile;
